import React, { Component } from 'react';
import './App.css';
import Counter from './Counter';

class App extends Component {

  state = {
    counter1 : 0,
    counter2 : 0,
    counter3 : 0
  }

    incVal = (count, counter) =>{
    counter++;
    this.setState({
      [count] : counter
    })
  }

  decVal = (count, counter) =>{
    counter--;
    this.setState({
      [count] : counter
    })
  }

  render() {
    return (
      <div className="App">
        <Counter inc={()=>{this.incVal(Object.keys(this.state)[0], this.state.counter1)}}
                 dec={()=>{this.decVal(Object.keys(this.state)[0], this.state.counter1)}} num={this.state.counter1}/>
        <Counter inc={()=>{this.incVal(Object.keys(this.state)[1], this.state.counter2)}}
                 dec={()=>{this.decVal(Object.keys(this.state)[1], this.state.counter2)}} num={this.state.counter2}/>
        <Counter inc={()=>{this.incVal(Object.keys(this.state)[2], this.state.counter3)}}
                 dec={()=>{this.decVal(Object.keys(this.state)[2], this.state.counter3)}} num={this.state.counter3}/>
      </div>
    );
  }
}
export default App;
