import React, { Component } from 'react';
import './App.css';
import Counter from './Counter';

class App extends Component {

  state = {
    counter : 0
  }

  incVal = () =>{
    this.state.counter++;
    this.setState({
      counter : this.state.counter
    })
  }

  decVal = () =>{
    this.state.counter--;
    this.setState({
      counter : this.state.counter
    })
  }

  render() {
    return (
      <div className="App">
        <Counter inc={this.incVal} dec={this.decVal}/>
      </div>
    );
  }
}
export default App;
