import React, { Component } from 'react';
import './App.css';
import Counter from './Counter';

class App extends Component {

  state = {
    counter1 : 0,
    counter2 : 0,
    counter3 : 0

  }

  incVal = (counter) =>{
    counter++;
    this.setState({
      counter : counter
    })
  }

  decVal = (counter) =>{
    counter--;
    this.setState({
      counter : counter
    })
  }

  render() {
    return (
      <div className="App">
        <Counter inc={()=>{this.incVal(this.state.counter1)}} dec={()=>{this.decVal(this.state.counter1)}} num={this.state.counter1}/>
        {/*<Counter inc={this.incVal} dec={this.decVal} num={this.state.counter2}/>*/}
        {/*<Counter inc={this.incVal} dec={this.decVal} num={this.state.counter3}/>*/}
      </div>
    );
  }
}
export default App;
